INSERT INTO IngredientAndQuantity VALUES (default , '761c9490-4348-41bc-824a-85ebe2d1fc46' , 'chicken breast or thigh (chopped into small chunks, or whole chicken pieces)' , '1' , 'pound')
INSERT INTO IngredientAndQuantity VALUES (default , '761c9490-4348-41bc-824a-85ebe2d1fc46' , 'potatoes (chopped into chunks)' , '2-3' , '')
INSERT INTO IngredientAndQuantity VALUES (default , '761c9490-4348-41bc-824a-85ebe2d1fc46' , 'Madras curry powder' , '2' , 'tablespoons')
INSERT INTO IngredientAndQuantity VALUES (default , '761c9490-4348-41bc-824a-85ebe2d1fc46' , 'bay leaves' , '2' , '')
INSERT INTO IngredientAndQuantity VALUES (default , '761c9490-4348-41bc-824a-85ebe2d1fc46' , 'chili flakes (or cayenne pepper, to taste)' , '0.5-1' , 'teaspoons')
INSERT INTO IngredientAndQuantity VALUES (default , '761c9490-4348-41bc-824a-85ebe2d1fc46' , 'shallots ' , '2' , '')
INSERT INTO IngredientAndQuantity VALUES (default , '761c9490-4348-41bc-824a-85ebe2d1fc46' , 'garlic (minced)' , '4-5' , 'cloves')
INSERT INTO IngredientAndQuantity VALUES (default , '761c9490-4348-41bc-824a-85ebe2d1fc46' , 'galangal (grated, or ginger)' , '1' , 'thumb-size piece')
INSERT INTO IngredientAndQuantity VALUES (default , '761c9490-4348-41bc-824a-85ebe2d1fc46' , 'chicken stock' , '0.5' , 'cup')
INSERT INTO IngredientAndQuantity VALUES (default , '761c9490-4348-41bc-824a-85ebe2d1fc46' , 'sugar' , '0.5' , 'teaspoons')
INSERT INTO IngredientAndQuantity VALUES (default , '761c9490-4348-41bc-824a-85ebe2d1fc46' , 'vegetable oil (for stir-frying)' , '2-3' , 'tablespoons')
INSERT INTO IngredientAndQuantity VALUES (default , '761c9490-4348-41bc-824a-85ebe2d1fc46' , 'tomatoes (chopped into chunks)' , '1-2' , 'medium size')
INSERT INTO IngredientAndQuantity VALUES (default , '761c9490-4348-41bc-824a-85ebe2d1fc46' , 'whole makrut lime leaves (available at Asian food stores)' , '1-2' , '')
