INSERT INTO Steps VALUES (1 , '08dd1b5f-8f34-469f-a2a0-d6338cd9b4ea' , 'Place the cabbage and carrot mixture in a large serving bowl.')
INSERT INTO Steps VALUES (2 , '08dd1b5f-8f34-469f-a2a0-d6338cd9b4ea' , 'In a small bowl, whisk together the mayonnaise, lemon juice, vinegar, 1/2 teaspoon sugar, and salt. Taste the dressing and add 1/2 teaspoon more sugar if you prefer a sweeter coleslaw.')
INSERT INTO Steps VALUES (3 , '08dd1b5f-8f34-469f-a2a0-d6338cd9b4ea' , 'Pour the dressing over the cabbage mixture and toss to coat.')
INSERT INTO Steps VALUES (4 , '08dd1b5f-8f34-469f-a2a0-d6338cd9b4ea' , 'Cover and refrigerate until ready to serve.')
